const httpProxy = require('http-proxy');
const http = require('http');

const proxy = httpProxy.createProxyServer({});
const nextURL = 'http://127.0.0.1:3000';
const mlURL = 'http://127.0.0.1:8000';

http.createServer((req, res) => {
  if (req.url.startsWith('/api-ml/')) {
    req.url = req.url.replace('/api-ml', '');
    proxy.web(req, res, { target: mlURL });
  } else {
    proxy.web(req, res, { target: nextURL });
  }
}).listen(5000, '0.0.0.0', () => console.log('Gateway proxy: http://0.0.0.0:5000'));

pages/api/council-stations.ts.content:
import type { NextApiRequest, NextApiResponse } from 'next';
import { pointInPolygon } from '../../utils/geo';

export const dynamic = 'force-dynamic';

interface Station {
  id: string;
  name: string;
  lat: number;
  lng: number;
  address?: string;
  borough?: string;
  [key: string]: any;
}

/**
 * API endpoint to get stations within a borough
 * Supports both borough name filtering and bbox/polygon filtering
 * 
 * Query params:
 * - borough: Borough name (optional)
 * - bbox: Bounding box as "minLng,minLat,maxLng,maxLat" (optional)
 * - polygon: GeoJSON polygon coordinates (optional)
 */
export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  if (req.method !== 'GET') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  const { borough, bbox, polygon } = req.query;

  try {
    // Fetch stations from main API
    const protocol = req.headers['x-forwarded-proto'] || 'http';
    const host = req.headers.host;
    const baseUrl = `${protocol}://${host}`;

    // Build stations API URL
    let stationsUrl = `${baseUrl}/api/stations`;
    if (bbox) {
      stationsUrl += `?bbox=${bbox}&tiles=2&limitPerTile=1000`;
    } else {
      // Default to full UK if no bbox
      stationsUrl += '?bbox=-8.649,49.823,1.763,60.845&tiles=4&limitPerTile=500';
    }

    console.log('[council-stations] Fetching from:', stationsUrl);

    const stationsResponse = await fetch(stationsUrl, {
      headers: { 'Cache-Control': 'no-cache' },
    });

    if (!stationsResponse.ok) {
      throw new Error(`Stations API failed: ${stationsResponse.status}`);
    }

    const stationsData = await stationsResponse.json();
    let stations: Station[] = stationsData.features.map((f: any) => ({
      id: f.properties.id || f.properties.UUID,
      name: f.properties.title || f.properties.AddressInfo?.Title || 'Unknown',
      lat: f.geometry.coordinates[1],
      lng: f.geometry.coordinates[0],
      address: f.properties.AddressInfo?.AddressLine1,
      borough: f.properties.AddressInfo?.Town || f.properties.AddressInfo?.County,
      ...f.properties,
    }));

    console.log('[council-stations] Total stations fetched:', stations.length);

    // Filter by borough name if provided
    if (borough && typeof borough === 'string') {
      const boroughLower = borough.toLowerCase();
      stations = stations.filter(s => {
        const stationBorough = (s.borough || '').toLowerCase();
        const stationName = (s.name || '').toLowerCase();
        const stationAddress = (s.address || '').toLowerCase();

        return (
          stationBorough.includes(boroughLower) ||
          stationName.includes(boroughLower) ||
          stationAddress.includes(boroughLower)
        );
      });

      console.log('[council-stations] Filtered by borough:', borough, '‚Üí', stations.length);
    }

    // Filter by polygon if provided
    if (polygon && typeof polygon === 'string') {
      try {
        const polygonCoords = JSON.parse(polygon);
        stations = stations.filter(s => 
          pointInPolygon([s.lng, s.lat], polygonCoords)
        );

        console.log('[council-stations] Filtered by polygon ‚Üí', stations.length);
      } catch (err) {
        console.error('[council-stations] Invalid polygon:', err);
      }
    }

    // Return as GeoJSON
    const response = {
      type: 'FeatureCollection',
      features: stations.map(s => ({
        type: 'Feature',
        geometry: {
          type: 'Point',
          coordinates: [s.lng, s.lat],
        },
        properties: {
          id: s.id,
          title: s.name,
          AddressInfo: {
            Title: s.name,
            AddressLine1: s.address,
            Town: s.borough,
          },
          isCouncil: true,
        },
      })),
      count: stations.length,
      source: 'council-filtered',
    };

    res.setHeader('Cache-Control', 'no-store, no-cache, must-revalidate');
    res.status(200).json(response);
  } catch (error) {
    console.error('[council-stations] Error:', error);
    res.status(500).json({ 
      error: 'Failed to fetch council stations',
      message: error instanceof Error ? error.message : 'Unknown error',
    });
  }
}

utils/geo.ts.content:
export const UK_BOUNDS = {
  west: -8.649,
  south: 49.823,
  east: 1.763,
  north: 60.845
};

export interface BBox {
  west: number;
  south: number;
  east: number;
  north: number;
}

export interface Tile {
  west: number;
  south: number;
  east: number;
  north: number;
  hash: string;
}

export function splitBBoxIntoTiles(bbox: BBox, tiles: number): Tile[] {
  const { west, south, east, north } = bbox;
  const latStep = (north - south) / tiles;
  const lngStep = (east - west) / tiles;

  const result: Tile[] = [];

  for (let row = 0; row < tiles; row++) {
    for (let col = 0; col < tiles; col++) {
      const tileWest = west + col * lngStep;
      const tileSouth = south + row * latStep;
      const tileEast = west + (col + 1) * lngStep;
      const tileNorth = south + (row + 1) * latStep;

      result.push({
        west: tileWest,
        south: tileSouth,
        east: tileEast,
        north: tileNorth,
        hash: generateTileHash(tileWest, tileSouth, tileEast, tileNorth)
      });
    }
  }

  return result;
}

export function generateTileHash(west: number, south: number, east: number, north: number): string {
  return `tile_${west.toFixed(3)}_${south.toFixed(3)}_${east.toFixed(3)}_${north.toFixed(3)}`;
}

export function parseBBox(bboxStr: string | null): BBox | null {
  if (!bboxStr) return null;

  const parts = bboxStr.split(',').map(parseFloat);
  if (parts.length !== 4 || parts.some(isNaN)) return null;

  return {
    west: parts[0],
    south: parts[1],
    east: parts[2],
    north: parts[3]
  };
}

export function calculateDistance(lat1: number, lng1: number, lat2: number, lng2: number): number {
  const R = 6371;
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lng2 - lng1) * Math.PI / 180;
  const a = 
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
    Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
}

export function pointInPolygon(point: [number, number], polygon: any): boolean {
  const [lng, lat] = point;

  const coords = polygon[0];
  if (!coords || coords.length < 3) return false;

  let inside = false;
  for (let i = 0, j = coords.length - 1; i < coords.length; j = i++) {
    const xi = coords[i][0], yi = coords[i][1];
    const xj = coords[j][0], yj = coords[j][1];

    const intersect = ((yi > lat) !== (yj > lat))
      && (lng < (xj - xi) * (lat - yi) / (yj - yi) + xi);
    if (intersect) inside = !inside;
  }

  return inside;
}

utils/haversine.ts.content:
/**
 * Calculate distance between two geographic coordinates using Haversine formula
 * Returns distance in kilometers
 */

interface Coordinates {
  lat: number;
  lng: number;
}

const EARTH_RADIUS_KM = 6371;

/**
 * Convert degrees to radians
 */
function toRadians(degrees: number): number {
  return degrees * (Math.PI / 180);
}

/**
 * Calculate distance between two points in kilometers
 * @param point1 First coordinate {lat, lng}
 * @param point2 Second coordinate {lat, lng}
 * @returns Distance in kilometers
 */
export function haversineDistance(point1: Coordinates, point2: Coordinates): number {
  const lat1 = toRadians(point1.lat);
  const lat2 = toRadians(point2.lat);
  const deltaLat = toRadians(point2.lat - point1.lat);
  const deltaLng = toRadians(point2.lng - point1.lng);

  const a =
    Math.sin(deltaLat / 2) * Math.sin(deltaLat / 2) +
    Math.cos(lat1) * Math.cos(lat2) *
    Math.sin(deltaLng / 2) * Math.sin(deltaLng / 2);

  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

  return EARTH_RADIUS_KM * c;
}

/**
 * Find nearest station from a list
 * @param userLocation User's current location
 * @param stations Array of stations with lat/lng
 * @returns Nearest station with distance
 */
export function findNearestStation(
  userLocation: Coordinates,
  stations: Array<{ id: string; lat: number; lng: number; [key: string]: any }>
): { station: any; distance: number } | null {
  if (!stations.length) return null;

  let nearest = stations[0];
  let minDistance = haversineDistance(userLocation, { lat: nearest.lat, lng: nearest.lng });

  for (let i = 1; i < stations.length; i++) {
    const distance = haversineDistance(userLocation, { 
      lat: stations[i].lat, 
      lng: stations[i].lng 
    });

    if (distance < minDistance) {
      minDistance = distance;
      nearest = stations[i];
    }
  }

  return { station: nearest, distance: minDistance };
}

/**
 * Format distance for display
 * @param km Distance in kilometers
 * @returns Formatted string (e.g., "1.2 km" or "350 m")
 */
export function formatDistance(km: number): string {
  if (km < 1) {
    return `${Math.round(km * 1000)} m`;
  }
  return `${km.toFixed(1)} km`;
}

