import { UK_BOUNDS } from '../utils/geo';

const NOMINATIM_BASE = 'https://nominatim.openstreetmap.org/search';
const UK_VIEWBOX = `${UK_BOUNDS.west},${UK_BOUNDS.north},${UK_BOUNDS.east},${UK_BOUNDS.south}`;

export async function geocodeUKBiased(searchTerm) {
  if (!searchTerm || !searchTerm.trim()) {
    return { error: 'Please enter a search term' };
  }

  const params = new URLSearchParams({
    q: searchTerm.trim(),
    format: 'json',
    countrycodes: 'gb',
    limit: '1',
    bounded: '1',
    viewbox: UK_VIEWBOX,
    addressdetails: '1'
  });

  try {
    const response = await fetch(`${NOMINATIM_BASE}?${params}`, {
      headers: {
        'User-Agent': 'Autodun-EV-Finder/1.0'
      }
    });

    if (!response.ok) {
      throw new Error(`Nominatim returned ${response.status}`);
    }

    const results = await response.json();

    if (!results || results.length === 0) {
      return { 
        error: 'Place not found in the UK',
        shouldShowToast: true 
      };
    }

    const result = results[0];
    const lat = parseFloat(result.lat);
    const lng = parseFloat(result.lon);

    if (isOutsideUK(lat, lng)) {
      console.warn('[geocode] Result outside UK bounds, clamping:', { lat, lng });
      const clamped = clampToUKBounds(lat, lng);
      return {
        ...result,
        lat: clamped.lat,
        lng: clamped.lng,
        display_name: result.display_name,
        boundingbox: result.boundingbox,
        address: result.address,
        clamped: true
      };
    }

    return {
      lat,
      lng,
      display_name: result.display_name,
      boundingbox: result.boundingbox,
      address: result.address
    };
  } catch (error) {
    console.error('[geocode] Nominatim error:', error);
    return { error: 'Search service unavailable. Please try again.' };
  }
}

export function isOutsideUK(lat, lng) {
  return (
    lat < UK_BOUNDS.south ||
    lat > UK_BOUNDS.north ||
    lng < UK_BOUNDS.west ||
    lng > UK_BOUNDS.east
  );
}

export function clampToUKBounds(lat, lng) {
  return {
    lat: Math.max(UK_BOUNDS.south, Math.min(UK_BOUNDS.north, lat)),
    lng: Math.max(UK_BOUNDS.west, Math.min(UK_BOUNDS.east, lng))
  };
}

export function extractRegionName(geocodeResult) {
  if (!geocodeResult || !geocodeResult.address) {
    return 'United Kingdom';
  }

  const addr = geocodeResult.address;

  if (addr.city) return addr.city;
  if (addr.town) return addr.town;
  if (addr.village) return addr.village;
  if (addr.county) return addr.county;
  if (addr.state) return addr.state;
  if (addr.region) return addr.region;

  return 'United Kingdom';
}

export async function geocodeFromQueryParam(qParam) {
  if (!qParam) return null;

  const result = await geocodeUKBiased(qParam);

  if (result.error) {
    console.warn('[geocode] Query param geocoding failed:', result.error);
    return null;
  }

  return result;
}

utils/telemetry.ts.content:
/**
 * Telemetry logging utility - captures anonymized, non-PII events
 * for later ML analysis. No-ops if no endpoint configured.
 */

export interface TelemetryEvent {
  name: string;
  payload: Record<string, any>;
  timestamp: number;
}

const TELEMETRY_ENABLED = typeof window !== 'undefined' && process.env.NEXT_PUBLIC_TELEMETRY_DISABLED !== 'true';

/**
 * Hash a string for anonymization (simple non-cryptographic hash)
 */
function simpleHash(str: string): string {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32bit integer
  }
  return Math.abs(hash).toString(16);
}

/**
 * Log a telemetry event
 * @param name Event name (e.g., 'search', 'feedback_submit', 'drawer_open')
 * @param payload Event data (must not contain PII)
 */
export function logEvent(name: string, payload: Record<string, any> = {}): void {
  if (!TELEMETRY_ENABLED) return;

  const event: TelemetryEvent = {
    name,
    payload: {
      ...payload,
      // Add session context (anonymized)
      sessionId: getSessionId(),
    },
    timestamp: Date.now(),
  };

  // Dev console logging
  console.log('[Telemetry]', event.name, event.payload);

  // Future: POST to /api/telemetry endpoint
  // if (TELEMETRY_ENDPOINT) {
  //   fetch('/api/telemetry', {
  //     method: 'POST',
  //     headers: { 'Content-Type': 'application/json' },
  //     body: JSON.stringify(event),
  //   }).catch(() => {});
  // }
}

/**
 * Get or create an anonymous session ID (stored in sessionStorage)
 */
function getSessionId(): string {
  if (typeof window === 'undefined') return 'server';

  const key = 'telemetry_session';
  let sessionId = sessionStorage.getItem(key);

  if (!sessionId) {
    sessionId = Math.random().toString(36).substring(2, 15);
    sessionStorage.setItem(key, sessionId);
  }

  return sessionId;
}

/**
 * Hash a search query for telemetry
 */
export function hashSearchQuery(query: string): string {
  return simpleHash(query.toLowerCase().trim());
}

/**
 * Telemetry event helpers
 */
export const telemetry = {
  search: (query: string, resultsCount: number) => 
    logEvent('search', { queryHash: hashSearchQuery(query), resultsCount }),

  drawerOpen: (stationId: string, isCouncil: boolean = false) => 
    logEvent('drawer_open', { stationId, isCouncil }),

  drawerClose: (stationId: string, durationMs: number) => 
    logEvent('drawer_close', { stationId, durationMs }),

  feedbackSubmit: (stationId: string, vote: 'good' | 'bad', hasComment: boolean) => 
    logEvent('feedback_submit', { stationId, vote, hasComment }),

  routeClicked: (stationId: string, provider: 'google' | 'apple') => 
    logEvent('route_clicked', { stationId, provider }),

  councilSelected: (borough: string, stationCount: number) => 
    logEvent('council_selected', { boroughHash: simpleHash(borough), stationCount }),

  locateMeClicked: (granted: boolean) => 
    logEvent('locate_me_clicked', { granted }),

  toggleLayer: (layer: 'heatmap' | 'markers' | 'council', visible: boolean) => 
    logEvent('toggle_layer', { layer, visible }),
};

lib/connectorCatalog.ts.content:
export const CONNECTOR_COLORS: Record<string, string> = {
  "CCS": "#3b82f6",       // blue
  "CHAdeMO": "#10b981",   // green
  "Type 2": "#8b5cf6",    // purple
};

interface ConnectorInput {
  type?: string;
  quantity?: number;
  powerKW?: number;
}

interface CanonicalConnector {
  label: string;
  quantity: number;
  maxPowerKW?: number;
}

export function aggregateToCanonical(
  connectors: ConnectorInput[]
): CanonicalConnector[] {
  const map = new Map<string, { quantity: number; maxPowerKW?: number }>();

  for (const c of connectors) {
    const type = c.type || "Unknown";
    const qty = typeof c.quantity === "number" ? c.quantity : 1;
    const kw = c.powerKW;

    if (!map.has(type)) {
      map.set(type, { quantity: 0 });
    }
    const entry = map.get(type)!;
    entry.quantity += qty;
    if (kw !== undefined && (entry.maxPowerKW === undefined || kw > entry.maxPowerKW)) {
      entry.maxPowerKW = kw;
    }
  }

  return Array.from(map.entries()).map(([label, data]) => ({
    label,
    quantity: data.quantity,
    maxPowerKW: data.maxPowerKW,
  }));
}
